function [res, C,D] = AutoCutOne(DATA, exname, ispk, args)    istart = now;    name = regexprep(exname, '\.p[0-9]*',sprintf('.p%d',ispk));    toplevel = DATA.toplevel;    if strcmp(DATA.autocutmode,'james')            elseif strcmp(DATA.autocutmode,'quick')[res,C,D] =  AllV.AllVPcs(name ,'nowatch',args{:},'tchan',ispk,'automode','james','toplevel',toplevel);        [res, C,D] =  AllV.AllVPcs(name ,'nowatch',args{:},'tchan',ispk,'Expt',DATA.Expt,'quickauto','toplevel',toplevel);    elseif strcmp(DATA.autocutmode,'ecker')        [res, C,D] =  AllV.AllVPcs(name ,'nowatch',args{:},'tchan',ispk,'Expt',DATA.Expt,'automode','ecker','toplevel',toplevel);    else        [res, C,D] =  AllV.AllVPcs(name ,'nowatch',args{:},'tchan',ispk,'saveautocut','toplevel',toplevel);    end    