function D = CondenseCluster(C)if isfield(C, 'cls') && isfield(C, 'workerid')    for j = 1:length(C.cls)        C.cls{j} = AllV.CondenseCluster(C.cls{j});    end    D = C;    if isfield(D,'args')        for j = 1:length(D.args)            if iscell(D.args{j})                D.args{j} = [];            end        end    endelseif isfield(C, 'cuts') && isfield(C, 'Clusters')    for j = 1:length(C.cuts)        C.cuts{j} = AllV.CondenseCluster(C.cuts{j});    end    D = C;elseif isfield(C, 'cluster') && isfield(C, 'pcs')    D = rmfields(C,{'pcs' 'Evec'});    D.cluster = rmfields(D.cluster,{'autofits' 'eckercluster'});else    D = rmfields(C,{'gmfit2dman' 'gmfit2d' 'gmfit1d' 'gmfit'});end    