function C = GetClusterDef(cluster, n, varargin)%C = AllV.GetClusterDef(cluster, n) return relevant member of Cluster struct    %can use GetClusterDef(Clsuters,p.c) where clusters is a cell arry, p.c is%double. or%%C = AllV.GetClusterDef(cluster, n, 'bynumber')findnumber = [];j = 1;while j <= length(varargin)    if strcmp(varargin{j},'bynumber')        j = j+1;        findnumber = varargin{j};    end    j = j+1;endif iscell(cluster)    Clusters = cluster;    p = floor(n);    cl = round(mod(n,1).*10);    cluster = Clusters{p};    n = cl;end    C = [];    if n == 1        C = cluster;    elseif n-1 <= length(cluster.next)        C = cluster.next{n-1};    else         C = cluster;    end    