function str = IDStr(DATA, varargin)strargs = {};j = 1;while j <= length(varargin)    if ischar(varargin{j})        strargs = {strargs{:} varargin{j}};    endj = j+1;endif isfield(DATA,'exptno')    str = sprintf('E%dP%d',DATA.exptno,AllV.ProbeNumber(DATA));else    str = sprintf('E?P%d',AllV.ProbeNumber(DATA));endif sum(strcmp('cluster',strargs))    if isfield(DATA,'currentcluster')        str = sprintf('%scl%d',str,DATA.currentcluster)    elseif isnumeric(DATA.cluster)        str = sprintf('%s.%d',str,DATA.cluster);    endend