function [V, DATA] = ReadSpikeFiles(DATA, name)%[DATA, V] = ReadSpikeFiles(DATA, name) Read In Spike Files%if DATA is an integer, use that as probe number    V.name = name;    args = {};    if isnumeric(DATA)        p = DATA;        clear DATA;        DATA.probe = p;        DATA.name = name;        DATA.clustersubdir = [];        DATA.exptno = GetExptNumber(DATA.name);    else        id = regexp(V.name,'Expt[0-9]*');        DATA.exptno = sscanf(V.name(id+4:end),'%d');    end    DATA.Expt.exptno = DATA.exptno;    if DATA.guistate.UseAutoClusters        args = {args{:} 'auto'};    end    s = AllV.SpkFileName(DATA,args{:});    V.Spikes = ReadSpikeFile(s,'allprobes');    V.exptno = DATA.exptno;    V.probe = DATA.probe;    if isfield(V.Spikes,'xchans')        V.probelist = [V.probe V.Spikes.xchans];    else        V.probelist = [V.probe];    end    [monk, monkey, mdir] = GetMonkeyName(DATA.name);    if isempty(V.Spikes)        return;    end    if V.Spikes.Header.bysuffix == 0    if isfield(V.Spikes,'matfile')        V.matfile = V.Spikes.matfile;    else        [a,b] = fileparts(DATA.name);        V.matfile = [a '/' monk mdir '.mat'];    end    end