function p = SelectProbe(a,b,p)%AllV.SelectProbe(a,b,p) Changes trigger probeit = findobj(gca,'Tag','ProbeLabel');if ~isempty(it)    set(it,'color','m');endDATA = GetDataFromFig(a);if strcmp(p,'set') && ~isempty(b)    p = get(b.NewValue,'UserData');elseif strcmp(p,'plot') && ~isempty(b)    p = get(b.NewValue,'UserData');    DATA.plotspk.probes = cat(2,p,DATA.plotspk.probes);    set(DATA.toplevel,'userData',DATA);    AllV.QuickSpks(DATA,1000);    return;endAllV.ChangeProbe(DATA,[],p);