function SetGUI(DATA, varargin)%AllV.SetGUI(DATA, varargin) Set Gui, or change components.%AllV.SetGUI(DATA, 'clearrevert') removes menu for reverting%clustersDATA = GetDataFromFig(DATA);        if DATA.interactive < 0        return;    end    j = 1;while j <= length(varargin)    if strcmp(varargin{j},'clearrevert')        ClearMenu(DATA, 'revert');        return;    end    j = j+1;end    onoff = {'off' 'on'};    c = get(DATA.toplevel,'Children');    SetMenuCheck(DATA.tag.spikes,'PlotByTrial',DATA.plotspk.bytrial);    SetMenuCheck(DATA.toplevel,'PlotISI',DATA.plot.isi);    SetMenuCheck(DATA.toplevel,'PlotExpt',DATA.plot.expt);    SetMenuCheck(DATA.toplevel,'advanceprobe',DATA.auto.advanceprobe);    SetMenuCheck(DATA.toplevel,'checkxcorr',DATA.auto.checkxcorr);    SetMenuCheck(DATA.toplevel,'csd',DATA.csd > 0);    SetMenuCheck(DATA.toplevel,'dvdy',DATA.csd == 1);    SetMenuCheck(DATA.toplevel,'dvdt',DATA.dvdt);    SetMenuCheck(DATA.toplevel,'ProbeModeMenu',DATA.probeswitchmode,'exclusive');    optionmenu = findobj(allchild(DATA.toplevel),'flat','tag','OptionMenu');    f = fields(DATA.auto);    for j = 1:length(f)        m = findobj(optionmenu,'tag',f{j});        if ~isempty(m)            SetMenuCheck(m,DATA.auto.(f{j}));        end    end    cmenu = findobj(allchild(optionmenu),'flat','tag','CutOptionMenu');    f = fields(DATA.quickcutmode);    for j = 1:length(f)        m = findobj(cmenu,'tag',f{j});        if ~isempty(m)            SetMenuCheck(m,DATA.quickcutmode.(f{j}));        end    end        clustermenu = findobj(allchild(DATA.toplevel),'flat','tag','ClusterMenu');    m = findobj(clustermenu,'Tag','xcorrprobes');    SetMenuCheck(m,DATA.plot.xcorrprobes);    SetMenuCheck(findobj(clustermenu,'Tag','plotexpt'),DATA.plot.expt);    cmenu = findobj(allchild(clustermenu),'flat','tag','PlotMenu');    f = fields(DATA.plot);    for j = 1:length(f)        m = findobj(cmenu,'tag',['plot' f{j}]);        if ~isempty(m)            SetMenuCheck(m,DATA.plot.(f{j}));        end    endfunction ClearMenu(DATA, str)x = AllV.findobj(DATA, 'revert');delete(x);