function pos = xyr2pos(xyr, aspect)%pos = AllV.xyr2pos(xyr, aspect)%aspect is used to convert from one aspect ratio to another.% and so is the ratio of the old aspectratio to the new oneif nargin == 1    aspect = 1;    xa = 1;    ya = 1;    elseif length(xyr) > 4   a = xyr(5);   xa = abs(cos(a).^2 + ((1./aspect))*sin(a).^2);   ya = abs(cos(a).^2 + (aspect)*sin(a).^2);   pos(5) = atan2(sin(a)./aspect,cos(a));endxyr(3:4) = abs(xyr(3:4));    pos(1) = xyr(1)-xyr(3).*xa;    pos(2) = xyr(2)-xyr(4).*ya;    pos(3) = xyr(1)+xyr(3).*xa;    pos(4) = xyr(2)+xyr(4).*ya;