function cmenu = AddCellContextMenu(DATA, type)%cmenu = PC.AddCellContextMenu(DATA, 'subplot|line')%builds a conted menu for setting Cell assignmentscmenu = uicontextmenu;    ncells = DATA.ncellmax;    if strcmp(type,'subplot')        for j = 1:ncells            uimenu(cmenu,'label',sprintf('Cell %d',j),'Callback',{@PC.SetCellFromSubplot, j});        end    else                uimenu(cmenu,'label',sprintf('Spike%d',j-1),'foregroundcolor',DATA.colors{j});        for k = 1:ncells            c = uimenu(cmenu,'label',sprintf('Cell %d',k),'Callback',{@PC.SetCellFromLine, j-1,  k});            if k == 1                set(c,'separator','on');            end        end    end    