function  h = AddTitle(DATA, C, titlemode, varargin)%h = AddTitle(DATA, C, titlemode, ...) Adds a title descirbing cluster%spstr = [];    p = C.probe(1);    xs = '';    tcolor = '';    j = 1;    cellno = 0;    while j <= length(varargin)        if strcmp(varargin{j},'cell')            j = j+1;            cellno = varargin{j};        end        j = j+1;    end        if isfield(C,'exptid')        eid = C.exptid;        if isfield(DATA,'muCellList') && DATA.muCellList(eid,p,C.cluster) > 0            xs = ' MU';            tcolor = 'r';        end    end      if titlemode == 2        if C.space(1) == 6            c = DATA.NDSpaceChars(C.space(2));        elseif C.space(1) == 0            c = 'a';        else            c = DATA.NDSpaceChars(C.space(1));        end        if cellno > 0            xs = [xs sprintf('Cell%d',cellno)];        end        h=title(sprintf('E%.0fP%s %.2f%s',C.exptno,PC.ProbeLabel(p,DATA,C.cluster),PC.DistanceMeasure(C, C.cluster,DATA.mahaltype),xs));        if ~isempty(tcolor)            set(h,'color',tcolor);        end    elseif titlemode == 1        h= title(sprintf('E%.0fP%s %.2f  %s %.2f',C.exptno,PC.ProbeLabel(p,DATA),C.mahal(1),spstr,C.mahal(4)));    else        h= title(sprintf('P%s Ex %.0f Gm %.2f  %s (%.2f,%.2f for space %.0f)%.0f',PC.ProbeLabel(p, DATA),C.exptno,C.mahal(1),spstr,C.mahal(4),C.bestspace(1),C.bestspace(2),C.sign));    end