function DATA = CalcDistances(DATA)    Clusters = getappdata(DATA.toplevel,'Clusters');    if ~isfield(DATA,'nclusters') %must not have loaded any data ye        cprintf('red','No Cluster data loaded yet\n');        DATA.mdistance = [];        DATA.matrixb = [];        return;    end    nc = max(DATA.nclusters(:));    tic;     for e = 1:length(Clusters)         for p = 1:length(Clusters{e})             for c = 1:nc                 d(e,p,c) = PC.DistanceMeasure(Clusters{e}{p},c,DATA.mahaltype);                 X(e,p,c) = clust.GetValue(Clusters{e}{p},c,DATA.measures{2});             end         end     end    toc    DATA.mdistance = d;    DATA.matrixb = X;    