    function CellChanged(DATA)        if DATA.show.watchallcellxy   %hit cell image plot            PC.PlotAllCellXY(DATA);        end        if DATA.show.watchallcellspks            PC.PlotAllCell(DATA,'allspkswithmean');        end        if DATA.show.watchallcellmean            PC.PlotAllCellMean(DATA,'meanlines');        end        it = findobj(allchild(DATA.fig.celllist),'flat','Tag','CellNumberId');        if ~isempty(it)            itpos = get(it,'position');            cl = caxis;            nc = size(colormap,1);            cmap = colormap;            cellid = get(it,'value');            [a,b] = find(DATA.CellList ==cellid);            ci = round(nc * (cellid-cl(1))./diff(cl));            if ci <= nc & ci > 0%backgroundcolor not shown except when item is activated, so not much use%'                set(it,'backgroundcolor',cmap(ci,:),'foregroundcolor',1-cmap(ci,:));                set(it,'backgroundcolor',[0.7 0.7 0.7],'foregroundcolor',[0 0 0]);                xi = findobj(allchild(gcf),'Tag','CellMarkerBox');                delete(xi);                axp = get(gca,'position')                x = axp(3)./size(DATA.CellList,2);                h = annotation('rectangle', itpos,'color',cmap(ci,:),'Tag','CellMarkerBox','linewidth',2);            end        end