function [d, drop, C] = DistanceMeasure(C, cl, type, varargin)    zeropad = 0;    j = 1;    while j <= length(varargin)        if strncmpi(varargin{j},'zeropad',6)            zeropad = 1;        end        j = j+1;    end    drop = NaN;    d = NaN;    if iscluster(C) ==2 %% cell array of Clusters        for j = 1:length(C)            [d(j), drop(j), C{j}] = PC.DistanceMeasure(C{j},cl,type,varargin{:});        end        return;    end    if ~iscluster(C)        return;    end    [C, ok] = PC.GetClusterInfo(C, cl);    if isnumeric(type)        strings = {'ND' '2D' 'XX' '1D' '2Gauss'};        type = strings{type};    end    if ok == 0        d = NaN;    elseif strcmp(type,'defaultisolation')        d = clust.GetValue(C,1,type);    elseif strcmp(type,'1D')         d = C.mahal(4);    elseif strcmp(type,'2D')         d =C.mahal(2);    elseif strcmp(type,'ND')         d =C.mahal(1);    elseif strcmp(type,'2Gauss')        if isfield(C,'fitdprime') %might need to add test of version # when + fitprime was good            d  = max([-C.fitdprime(1) 0]);        end    elseif strcmpi(type,'LRatio') && isfield(C,'lratio')        d = C.lratio;    elseif strcmpi(type,'isolation distance') && isfield(C,'isolation_distance')        d = C.isolation_distance;    elseif strcmpi(type,'myisolation') && isfield(C,'isolation')        d = C.isolation(1);    elseif strncmpi(type,'bestisolation',10) && isfield(C,'bestisolation')        if isfield(C.bestisolation,'isolation')            d = C.bestisolation.isolation(1);        elseif isfield(C,'isolation');                d = C.isolation(1);        else %some old autocut files.            d = C.mahal(2);        end    elseif strncmpi(type,'bestisolation',10) && isfield(C,'bestspace')            d = C.bestspace(1);            if d == 0 %not for real                d = NaN;            end    end    if isfield(C,'dropi') && length(C.dropi) > 2        drop = C.dropi(3);    end    if zeropad && isnan(d)        d = 0;    end                