function h = DrawBoxes(DATA, imtype, varargin)hname = sprintf('Box%dHandles');bh = getappdata(gcf,hname);useautolist = 0;passarg = ones(size(varargin));j = 1;while j <= length(varargin)    if strcmpi(varargin{j},'autolist')        useautolist = 1;        passarg(j) = 0;    elseif strcmpi(varargin{j},'clear')        for k = 1:length(bh)            if myhandle(bh(k));                delete(bh(k));            end        end        bh = [];        passarg(j) = 0;    end    j = j+1;end  if useautolist    [e,p] = find(DATA.selectautoprobe > 0);else   [e,p] = find(DATA.selectprobe);endif isempty(e)    h = [];    return;end   for j = 1:length(e)       h(j) = PC.DrawBox(e(j),p(j),imtype, varargin{find(passarg)});   end   bh = [bh h];   setappdata(gcf,hname,bh);   