function HitPopPoint(a,b, ex, p, cell, clid)DATA = GetDataFromFig(a);Clusters = getappdata(DATA.toplevel,'Clusters');if nargin < 5    clid = 1;endC = Clusters{ex}{p};DATA.selectprobe = zeros(length(Clusters),DATA.nprobes);DATA.selectprobe(ex,p) = 1;if nargin > 4     if strcmp(cell, 'clid')        DATA.currentcluster = clid;        cell = 0;    elseif  cell > 0        DATA.currentcell = cell;    endendfprintf('Expt %d Probe %d cl%d',C.exptno,p,clid);if cell > 0    fprintf('Cell %d\n',cell);elseif PC.isacell(DATA,C.exptid,p,clid)    [~,cell] = PC.isacell(DATA,C.exptid,p,clid);    fprintf(' is Cell %d\n',cell);else    fprintf('\n');endPC.ShowData(DATA, ex, p);