function DATA = MarkCurrentCluster(DATA)if strmatch(DATA.plot.alltype,{'template' 'mahal+var'})    PC.SetFigure(DATA, DATA.tag.all);    square = [-.5 0.5 0.5 -0.5 -0.5; -0.5 -0.5 0.5 0.5 -0.5];    hold on;    if ishandle(DATA.markh)        delete(DATA.markh);    end    DATA.markh = plot(square(1,:)+DATA.currentpoint(2),square(2,:)+DATA.currentpoint(1),'k-','linewidth',2);% really need this? Slows us down....    %    cmenu = PC.AddContextMenu(DATA,'cellplot');%    set(DATA.markh,'buttondownfcn',{@PC.HitImage,1},'uicontextmenu',cmenu);else     DATA.markh = [];end