function DATA = SaveCellList(DATA, varargin)backup = 0;j = 1;while j <= length(varargin)    if strncmpi(varargin{j},'backup',4)        backup = 1;    end    j = j+1;end    cellfile = [DATA.name '/CellList.mat'];    if isfield(DATA.CellDetails,'listtype')         if strcmp(DATA.CellDetails.listtype,'auto');            cellfile = [DATA.name '/AutoCellList.mat'];        end    end    if DATA.guistate.celllistsaves > 50        DATA.guistate.celllistsaves = 0;    end    if DATA.guistate.celllistsaves == 0        backup = 1;    end    DATA.guistate.celllistsaves = DATA.guistate.celllistsaves +1;    SetData(DATA);    if backup        BackupFile(cellfile);    end    X.CellList = DATA.CellList;    X.CellDetails = DATA.CellDetails;    X.CellChanges = DATA.CellChanges;    if isfield(DATA,'fitjumps')        CellDetails.fitjumps = DATA.fitjumps;    end    if isfield(DATA,'muCellList')        X.muCellList = DATA.muCellList;        sz = size(DATA.CellList);        if size(X.muCellList,1) < size(DATA.CellList,1) || size(X.muCellList,2) < size(DATA.CellList,2)            X.muCellList(sz(1),sz(2),:) = 0;        end    end    save(cellfile, '-struct','X');