function DATA = SpoolCurrentSpikes(mn,b, varargin)strargs = cell2cellstr(varargin);        DATA = GetDataFromFig(mn);    AllSpikes = PC.CheckAllSpikes(DATA,DATA.currentpoint(1),DATA.currentpoint(2),'allprobes');    DATA = get(DATA.toplevel,'UserData');    DATA.spoolfig = gcf;    if isempty(AllSpikes)        a = PC.SpoolSpikeFile(DATA,DATA.currentpoint(1),DATA.currentpoint(2));        if isfield(a,'svfig')            DATA.spoolfig = a.svfig;            set(DATA.toplevel,'UserData',DATA);        end    else        X = getappdata(gcbf,'SpikeData');        if isfield(X,'e')            DATA.currentpoint(1) = X.e;            DATA.currentpoint(2) = X.p;        end        DATA = PC.SetExpt(DATA, DATA.currentpoint(1));        C = PC.GetClusterInfo(DATA,[DATA.currentpoint 1]);        PC.SetFigure(DATA,DATA.tag.xyplot,'front');        hold off;        if strcmp('showxy',strargs);            GetFigure(DATA.tag.xyplot);            xl = get(gca,'xlim');            yl = get(gca,'ylim');            hold off;            plots = PC.PlotClusterPoints(C,1:10,DATA.currentcluster);            hold on;            set(gca,'xlim',xl,'ylim',yl,'xlimmode','manual','ylimmode','manual');            gca        else            PC.PlotClusterXY(DATA,C);        end        drawnow;        PC.SetTrialList(DATA,C,DATA.currenttrial);        PC.SpoolSpikes(DATA, DATA.currentpoint,strargs{:});    end            