function dup = isduplicate(DATA, row, p, cl, varargin)%dup = PC.isduplicate(DATA, row, p, cl, varargin)%if CellList(row,p,cl) is a duplicate of another cell, returns 1. Else 0.listtype = 'default';j = 1;while j <= length(varargin)    if strcmp(varargin{j},'autolist')        listtype = 'autolist';    end    j = j+1;endif strcmp(listtype,'autolist')    CellList = DATA.autolist.CellList;else    CellList = DATA.CellList;end    if size(CellList,3) < cl || CellList(row,p,cl) >= 0    dup = 0;else    dup = -CellList(row,p,cl);end    